SELECT t1.node_name nodeName,
sum(t2.${counter}) ${counter}
FROM ( SELECT cell_name, node_name FROM unicom_cell_wcdma ) t1 left JOIN ( SELECT name, time,
${counter}
FROM unicom_counter_history_wcdma ) t2 on t1.cell_name = t2.name WHERE t2.time = #{time} GROUP BY t1.node_name

